mapper.select:
select

from tb_newbee_mall_coupon
where is_deleted = 0

    and coupon_name LIKE CONCAT('%',#{couponName},'%')


    and coupon_type = #{couponType}


    and coupon_status = #{couponStatus}


    and DATE_FORMAT(create_time,'%Y-%m-%d')  >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')


    and DATE_FORMAT(create_time,'%Y-%m-%d')  <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')

order by create_time desc

    limit #{start},#{limit}

select count(*) from tb_newbee_mall_coupon where is_deleted = 0
and coupon_name LIKE CONCAT('%',#{couponName},'%')
and coupon_type = #{couponType}
and coupon_status = #{couponStatus}
and DATE_FORMAT(create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')

mapper.update:
update tb_newbee_mall_coupon
set is_deleted=1,update_time=now() where coupon_id in

    #{id}

mapper.select:
select
from tb_newbee_mall_coupon where coupon_id in
#{id}
